        <button onClick={() => editUser()}>Edit user</button>

        {user.user.id ?
            <ul>
                <UserServices idUser={user.user.id}></UserServices>
            </ul>
            : "No hay servicios creados por este usuario"}
        <div>{new Date((user.user.createdAt)).toLocaleString()}</div>
    </> : <ErrorMessage />
}
