/* import { useNavigate } from "react-router-dom"; */
import { editUserService } from "../../services";

export const MyUserPage = () => {

    const [showPopUp, setShowPopUp] = useState(false);
    const [error, setError] = useState("");
    /* const navigate = useNavigate(); */
    const [biography, setBiography] = useState(undefined);
    const [avatar, setAvatar] = useState(undefined);
    const { user, token } = useContext(AuthContext);

    const handleForm = async (e) => {
        e.preventDefault();
        setError("")

        try {
            await editUserService({ biography, avatar, token })

            /* navigate("/"); */

        } catch (error) {
            setError(error.message)
        }
    }

    return /* user ?  */<>


        <section>
            <form onSubmit={handleForm}>
                <fieldset>
                    <legend>Set yout changes</legend>
                    <ul>
                        <li>
                            <label htmlFor="biography">Username: </label>
                            <input
                                type="text"
                                name="biography"
                                id="biography"
                                value={biography}
                                autoFocus
                                required
                                onChange={(e) => setBiography(e.target.value)}
                            />
                        </li>
                        <li>
                            <input type="file" name="file" /> onChange={(e) => setAvatar(e.target.value)}
                        </li>

                    </ul>
                </fieldset>
                <button>Actualiza tus cambios</button>
                {error ? <p>{error}</p> : null}
            </form>
        </section>

</>



