import { useContext, useState } from "react";
import { ErrorMessage } from "../errorMessage/ErrorMessage";

export const EditUser = ({ setShowPopUp, setBiography, setAvatar, token }) => {

    const modifyData = async (e) => {
        e.preventDefault();
        try {
            const data = {};

            data.avatar = new FormData();

            data.avatar.append("avatar", e.target.files[0]);
            data.biography = e.target.elements.biography.value;

            console.log("hola, antes de respuesta");

            const response = await fetch(`${process.env.REACT_APP_BACKEND}/users`, {
                method: 'PUT',
                body: data,
                headers: {
                    Authorization: token,
                    'Content-Type': 'application/json'
                }
            })


            if (response.ok) {
                console.log("cambios realizados");
            } else {
                console.error("no se ha actualizado")
            }
        } catch (error) {
            <ErrorMessage />
        }

    }

    return (
        <ul>
            <li>
                <label htmlFor="biography">Actualiza tu biografía</label>
                <input
                    type="textare"
                    name="biography"
                    id="biography"
                    required
                    onChange={(e) => setBiography(e.target.biography)}
                    placeholder="Actualiza tu biografía"
                />
            </li>

            <li>
                <input type="file" name="file"
                    onChange={(e) => setAvatar(e.target.value)}
                />
            </li>
            <button onClick={modifyData}>Save</button>
            <button onClick={() => setShowPopUp(false)} >Close</button>
        </ul>
    );
};
