import { useParams } from "react-router-dom";
import { useService } from "../../hooks/useService";
import { ErrorMessage } from "../../../src/components/errorMessage/ErrorMessage"
import { Service } from "../../components/service/Service";
import { useContext } from "react";
import { AuthContext } from "../../context/AuthContext";
import { NewServicePage } from "./NewServicePage";

export const ServicePage = () => {

    const { id } = useParams();
    const { service, loading, error, addService } = useService(id);
    const { user } = useContext(AuthContext);

    console.log('esto es el service: ', service);

    if (loading) return (<p>cargando UN SOLO servcicio....</p>);
    if (error) return (<ErrorMessage message={error} />)



    return (


        <section>

            <h1>Service from {service.username}</h1>

            {user ? <NewServicePage addService={addService} /> : null}



        </section>

    )

}
