import { useState } from "react";
import { sendRepliesService } from "../../../src/services/index";
import { useContext } from "react";
import { AuthContext } from "../../../src/context/AuthContext";

export const NewReply = ({ addService }) => {
  const [error, setError] = useState("");
  const [reply, setReply] = useState(false);
  const { token } = useContext(AuthContext);

  const handleForm = async (e) => {
    e.preventDefault();

    try {
      setReply(true);
      const data = new FormData(e.target);
      const newReply = await sendRepliesService({ data, token });
      /* HAY QUE DEVOVLER EL ID DEL SERVICIO, AHORA MISMO DEVUELVE "created service" */
      console.log("que é", newReply);

      e.target.reset();
    } catch (error) {
      setError(error.message);
    } finally {
      setReply(false);
    }
  };
  return (
    <>
      <form onSubmit={handleForm}>
        <fieldset>
          <legend>New Service</legend>
          <ul>
            <li>
              <label htmlFor="">Title: </label>
              <input
                type="text"
                name="title"
                id="titleService"
                placeholder="Name your service"
                autoFocus
                required
              />
            </li>
            <li>
              <label htmlFor="category">Category</label>
              <select name="category" id="category">
                <option value="Programming and Development">
                  Programming and Development
                </option>
                <option value="Design and art">Design and art</option>
                <option value="Music and Audio">Music and Audio</option>
                <option value="Video and Animation">Video and Animation</option>
                <option value="Writing and Translation">
                  Writing and Translation
                </option>
                <option value="Administrative and Secretary">
                  Administrative and Secretary
                </option>
                <option value="Digital Marketing">Digital Marketing</option>
                <option value="Business">Business</option>
                <option value="Various">Various</option>
              </select>
            </li>

            <li>
              <label htmlFor="descriptionService">Description:</label>
              <textarea
                name="description"
                id="descriptionService"
                rows="10"
                cols="80"
                placeholder="Describe your service"
              ></textarea>
            </li>

            <li>
              <input type="file" name="file" />
            </li>
          </ul>
        </fieldset>
        <button type="submit" value="submit">
          Add Service
        </button>

        {reply ? <p>Adding Service</p> : null}

        {error ? <p>{error}</p> : null}
      </form>
    </>
  );
};
