import { useParams } from "react-router-dom";
import useUser from "../../hooks/useUser";
import { ErrorMessage } from "../errorMessage/ErrorMessage";
import { Loading } from "../loading/Loading";
import { UserServices } from "../userServices/UserServices";



export const Profile = () => {
    const { id } = useParams();

    const { user, loading, error } = useUser(id);


    if (loading) return <Loading />;
    if (error) return <ErrorMessage message={error} />;



    return <section>
        {console.log("hola", user.user.id)};
        <h1>{user.user.username}</h1>


        {/* Por qué funciona así y no `${process.env.REACT_APP_BACKEND}/uploads/${user.avatar}` */}
        <img
            src={`${process.env.REACT_APP_BACKEND}/${user.user.avatar}`}
            alt="avatar"
            width={100}
        />

        <p>
            {user.user.biography}
        </p>
        <p>
            {user.user.email}
        </p>
        {/* Me falta filtrar por id de usuario */}
        <p>Created at {new Date((user.user.createdAt)).toLocaleString()}</p>
        <UserServices id={user.user.id} />





    </section>
}
